import React, { useState } from "react";
import './attendence.css'
import InputField from "../../components/InputField";
import ButtonField from "../../components/ButtonField";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const AttendanceSheet = () => {
  const [hidden, setHidden] = useState(true);
  const [attendance, setAttendance] = useState("");
  const [startDate, setStartDate] = useState(new Date());
  const today = new Date();
  const date1 = new Date(startDate);
  const today1 = new Date().getDate().toString();
  const options: any = { day: "numeric", month: "long", year: "numeric" };
  const formattedDate = today.toLocaleDateString("en-US", options);
  console.log(start)
  const monthYearString = date1.toLocaleString("default", {
    month: "long",
  });
  const numberOfDaysInMonth = new Date(
    date1.getFullYear(),
    date1.getMonth() + 1,
    0
  ).getDate();
  const monthDays = Array.from(
    { length: numberOfDaysInMonth },
    (_, i) => `${i + 1} ${monthYearString}`
  );

  return (
    <>
      <div className="d-flex p-3 flex-column">
        <div className="first d-flex flex-row">
          <div className="w-75">
            <p>{formattedDate}</p>
          </div>
          <div className="center " style={{ width: '220px', border: "1px solid black", borderRadius: "10px" }}>
            <DatePicker
              icon="fa fa-calendar"
              selected={startDate}
              onChange={(date: any) => setStartDate(date)}
              dateFormat="MMMM yyyy"
              showMonthYearPicker
              monthsShown={1}
            />
          </div>
        </div>
        <div className="second w-100 " style={{ overflowY: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th className="fixed-width">Eid</th>
                <th className="fixed-width">Name</th>
                <th className="fixed-width">D.O.B</th>
                <th className="fixed-width">D.O.J</th>
                <th className="expandable justify-content-end align-items-end d-flex">
                  Attendance
                </th>
                {formattedDate == today1 && (
                  <>
                {monthDays.map((day: any) => (
                  <th style={{ minWidth: "140px" }} key={day}>
                    {day}
                  </th>
                ))}
              </>
                )}

            </tr>
          </thead>
          <tbody>
            <tr>
              <th className="fixed-width" scope="row">
                1
              </th>
              <td className="fixed-width">Mark</td>
              <td className="fixed-width">Otto</td>
              <td className="fixed-width">@mdo</td>
              <td
                className={`expandable d-flex justify-content-end align-items-end ${attendance === "Present" ? "text-success" : "text-danger"
                  }`}
              >
                {hidden && (
                  <>
                    <ButtonField
                      label="Absent"
                      onClick={() => {
                        setAttendance("Absent");
                        setHidden(false);
                      }}
                      classname={"btn btn-outline-danger"}
                    />
                    <ButtonField
                      label="Present"
                      onClick={() => {
                        setAttendance("Present");
                        setHidden(false);
                      }}
                      classname={"btn btn-success"}
                    />
                  </>
                )}
                {attendance === "" ? "" : attendance}
              </td>
              <td className="align-content-center">
                <div className="dropdown">
                  <button className="dropbtn btn btn-light">
                    <i className="fa fa-ellipsis-v"></i>
                  </button>
                  <div className="dropdown-content">
                    <a href="#"><i className="fa fa-sun-o"></i>Half Day</a>
                    <a href="#"><i className="fa fa-sun-o"></i>Early Leave</a>
                  </div>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div >
    </>
  );
};

export default AttendanceSheet;
