import React, { useState } from 'react';
import imagePath from '../constants/imagePath';
import './accordion.css';
import { useNavigate } from 'react-router-dom';

interface Accordion {
    photo?: string;
    name?: string;
    gmail?: string;
    technology?: string;
    education?: string;
    educationYear?: string;
    skills?: string;
    experience?: string;
    recentEmployee?: string;
    portfolioLink?: string;
}

interface AccordionProps {
    data?: Accordion[];
}

const Accordion = ({ data }: AccordionProps) => {
    const navigate = useNavigate();
    const [openIndex, setOpenIndex] = useState<number | null>(null);

    const handleClick = (index: number) => {
        setOpenIndex(openIndex === index ? null : index);
    };

    return (
        <div className="accordion-container">
            {data && (
                <div className="accordion w-100" id="accordionExample">
                    <table className="table table-striped table-responsive">
                        <thead>
                            <tr>
                                <th></th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Technology</th>
                                <th>Education Year</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((item, index) => (
                                <React.Fragment key={index}>
                                    <tr
                                        className={`accordion-item ${openIndex === index ? 'open' : ''}`}
                                        onClick={() => handleClick(index)}
                                    >
                                        <td>
                                            <i className={`bx ${openIndex === index ? 'bxs-chevron-down' : 'bxs-chevron-right'} arrow`} />
                                        </td>
                                        <td>{item.name}</td>
                                        <td>{item.gmail}</td>
                                        <td>{item.technology}</td>
                                        <td>{item.educationYear}</td>
                                        <td>
                                            <i className="fa fa-ellipsis-v"></i>
                                        </td>
                                    </tr>
                                    {openIndex === index && (
                                        <tr className="accordion-collapse">
                                            <td colSpan={6}>
                                                <div className="accordion-body">
                                                    <div className="accordion-profile d-flex gap-2 justify-content-center align-items-center flex-row">
                                                        <div className="profile-image d-flex flex-column w-25 justify-content-center align-items-center">
                                                            <img className="profile-img " src={imagePath.profilelogo} alt="Profile" />
                                                            <div className="d-flex flex-column justify-content-center align-items-center p-2">
                                                                    <div className="label">{item.name}</div>
                                                                    <div className="value">{item.technology}</div>
                                                            </div>
                                                        </div>
                                                        <div className="profile-detail w-75">
                                                            <div className='gap-3'>
                                                                <div className="d-flex gap-3 flex-row mt-3">
                                                                    <div className="label">Education</div>
                                                                    <div className="value">{item.education}</div>
                                                                </div>
                                                                <div className="d-flex gap-3 flex-row  mt-3">
                                                                    <div className="label">Email Id</div>
                                                                    <div className="value">{item.gmail}</div>
                                                                </div>
                                                                <div className="d-flex gap-3 flex-row  mt-3">
                                                                    <div className="label">Skills</div>
                                                                    <div className="value">{item.skills}</div>
                                                                </div>
                                                                <div className="d-flex gap-3 flex-row mt-3">
                                                                    <div className="label">Date Of Joining:</div>
                                                                    <div className="value">15/07/2024</div>
                                                                </div>
                                                                <div className="d-flex gap-3 flex-row  mt-3">
                                                                    <div className="label">Contact No:</div>
                                                                    <div className="value">6260335083</div>
                                                                </div>
                                                                <div className="d-flex gap-3 flex-row  mt-3">
                                                                    <div className="label">Email:</div>
                                                                    <div className="value">mohit@gmail.com</div>
                                                                </div>
                                                                <div className="d-flex gap-3 flex-row  mt-3">
                                                                    <div className="label">Email:</div>
                                                                    <div className="value">mohit@gmail.com</div>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </td>
                                        </tr>
                                    )}
                                </React.Fragment>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default Accordion;
