import React, { useState } from "react";
import InputField from "./InputField";
import ButtonField from "./ButtonField";
import "./Form.css";
import imagePath from "../constants/imagePath";

const FormProps = () => {
  const [formData, setFormData] = useState({
    employeeId: "mohit",
    firstName: "",
    lastName: "",
    email: "",
    phoneNo: "",
    resigningDate: "",
    jobTitle: "",
    files: "",
  });

  const handleFileChange = (e:any) => {
    setFormData({ ...formData, files: e.target.value });
  };

  const handleInputChange = (field:any) => (e:any) => {
    setFormData({ ...formData, [field]: e.target.value });
  };

  return (
    <div className="modal" id="exampleModal" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Form Heading</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div className="modal-body d-flex flex-column">
                  <div className="p-2">
                    <h5 className="fontW500_blackbd">Upload Profile</h5>
                      <div className="d-flex gap-4">
                        <img src={imagePath.profile} alt="Profile" />
                        <div className="d-flex flex-row justify-content-center align-items-center">
                          <input
                            type="file"
                            accept="image/*"
                            onChange={handleFileChange}
                            style={{ display: "none" }}
                            id="imagePickerInput"
                          />
                          <label
                            htmlFor="imagePickerInput"
                            className="btn btn-warning text-white"
                          >
                            Upload
                          </label>
                        </div>
                      </div>
                    )}
                <div className="d-flex flex-wrap p-2 gap-3">
                  <InputField label="First Name" onChange={handleInputChange("firstName")} />
                  <InputField label="Last Name" onChange={handleInputChange("lastName")} />
                  <InputField label="Email" onChange={handleInputChange("email")} />
                  <InputField label="Phone No" onChange={handleInputChange("phoneNo")} />
                </div>
              </div>
            </div>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-outline-warning" data-bs-dismiss="modal">Close</button>
            <ButtonField classname="btn btn-warning text-white" label="Save changes" />
          </div>
        </div>
      </div>

  );
};

export default FormProps;
