import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


const ForgotPasswordForm = () => {
    const [hiddenForgetFrom,setHiddenForgetFrom]=useState<boolean>(true);
  const [email, setEmail] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (event:any) => {
    event.preventDefault();
    try {
        console.log(email);

      const response = await axios.post('http://localhost:3000/mail', { email });
      console.log(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleBack = () => {
   navigate(-1);
  };

  return (<>
    {hiddenForgetFrom&&<div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h3 className="card-title text-center">Forgot Password</h3>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">Email address</label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <button type="submit" className="btn btn-primary" onClick={()=>{setHiddenForgetFrom(false)}}>Submit</button>
                <button type="button" className="btn btn-secondary ms-2" onClick={handleBack}>Back</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>}
  </>

  );
};

export default ForgotPasswordForm;
