import React, { useState } from "react";
import imagePath from "../../constants/imagePath";
import InputFeild from "../../components/InputField";
import ButtonField from "../../components/ButtonField";
import colors from "../../style/colors";
import "./loginPage.css";
import { useNavigate } from "react-router-dom";
const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const handleClickButton = () => {
    alert("Hello GoodEvening ");
  };
  return (
    <form action="/#" method="post">
      <div className="main-container">
        <div className="left-container center">
          <img className="abstract" src={imagePath.abstract}></img>
        </div>
        <div className="right-container center">
          <div className="right-container-first center">
            <img className="logo" src={imagePath.logo}></img>
          </div>
          <div className="right-container-second">
            <div className="login-form">
              <h4 className="fontS27" style={{ color: colors.yellow }}>
                Login{" "}
              </h4>
              <InputFeild
                classname="inputSize364 "
                labelclass="fontW500_black"
                label="Email Address"
                placeholder="Enter Email Address"
                onChange={(e) => {
                  setEmail(e.target.value);
                }}
              />
              <InputFeild
                classname="inputSize364 "
                labelclass="fontW500_black"
                label="Password"
                placeholder="Enter Password"
                onChange={(e) => {
                  setPassword(e.target.value);
                }}
              />
              <span className="d-block">
                <a
                  className="text-decoration-none fontW500_black"
                  style={{ color: colors.yellow }}
                  href="/"
                >
                  Forgot password?
                </a>
              </span>
              <ButtonField
                type="button"
                classname="buttonSize364_yellow"
                label="Log in"
                onClick={() => navigate('/maincomponent')}
              />
              <span className="d-block  ps-3 fontW400_black" onClick={() => navigate('/signup')}>
                Donâ€™t have an account?{" "}
                <a
                  className="text-decoration-none fontS14"
                  style={{ color: colors.yellow }}
                  href="#"
                >
                  {" "}
                  Create an account
                </a>
              </span>
            </div>
          </div>
        </div>
      </div>
    </form>
  );
};

export default LoginPage;
