import axios from 'axios';
import React, { useState } from 'react';
import OTPInput from 'react-otp-input';
import { useNavigate } from 'react-router-dom';
import InputField from '../../components/InputField';
import ButtonField from '../../components/ButtonField';

const ForgotPasswordForm = () => {
  const [hiddenForgetFrom, setHiddenForgetFrom] = useState<boolean>(true);
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const navigate = useNavigate();
  const handleOtpSubmit = async (event: any) => {
    event.preventDefault();
    try {
      console.log(email);

      const response = await axios.post('http://localhost:8080/otp', { email, otp });
      console.log(response.data.result);
      { response.data.result && navigate("/signin") }
    } catch (error) {
      console.log(error);
    }
  };


  const handleEmailSubmit = async (event: any) => {
    event.preventDefault();

    try {
      console.log(email);
      setHiddenForgetFrom(false);
      const response = await axios.post('http://localhost:8080/mail', { email });
      console.log(response.data);
    } catch (error) {
      console.log(error);
    }

  };

  const handleBack = () => {
    navigate(-1);
  };

  return (<>
    {hiddenForgetFrom && <div className="container mt-5 ">
      <div className="row justify-content-center">
        <div className="col-md-5">
          <div className="card">
            <div className="card-body">
              <h3 className="card-title text-warning text-center">Forgot Password</h3>
              <form onSubmit={handleEmailSubmit}>
                <div className="mb-3">
                  <InputField
                  classname="inputSize364 form-control"
                  labelclass="fontW500_black"
                  label="Email Address"
                  placeholder="Enter Email Address"
                  value={email}
                  onChange={(e:any) => setEmail(e.target.value)}
                />
                </div>
                <ButtonField type='submit' classname='buttonSize136_yellow' label='Submit' />
                <button type="button" className="btn btn-secondary ms-2" onClick={handleBack}>Back</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>}
    {!hiddenForgetFrom && <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-5">
          <div className="card">
            <div className="card-header">
              <h4 className='text-warning'>Enter OTP</h4>
            </div>
            <div className="card-body">
              <form onSubmit={handleOtpSubmit}>
                <div className="mb-3">
                  <label htmlFor="otp" className="form-label">OTP</label>
                  <OTPInput
                    value={otp}
                    onChange={setOtp}
                    numInputs={4}
                    renderSeparator={<span>-</span>}
                    renderInput={(props) => <input {...props}  className=''/>}
                  />
                </div>
                <ButtonField type='submit' classname='buttonSize364_yellow' label='Submit' />
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>}
  </>

  );
};

export default ForgotPasswordForm;
