import React from 'react';
import imagePath from '../constants/imagePath';
import { useNavigate } from 'react-router-dom';

interface HeaderProps {
  label?: string
  toggleSidebar?: React.MouseEventHandler<HTMLElement>;
  setHiddenSidebar?:React.Dispatch<React.SetStateAction<boolean>>;
}

const Header: React.FC<HeaderProps> = ({ toggleSidebar, label ,setHiddenSidebar}) => {
  const navigate = useNavigate();
  return (
    <>
      <div className="home-content"  >
        <div className='d-flex'>
          <i className='bx bx-menu' onClick={toggleSidebar}></i>
          <span className="text">{label}</span>
        </div>

        <div className='d-flex '>
          <span className='pe-3 py-2 d-lg-block d-md-block d-sm-block d-none'><i className="fa fa-search"></i></span>
          <img onClick={()=>{setHiddenSidebar && setHiddenSidebar((item)=>!item),navigate('/maincomponent/profile')}} style={{width:"50%"}} src={imagePath.profile} alt="Profile" />
        </div>
      </div>

    </>
  );
};

export default Header;
