import React from "react";
import imagePath from "../constants/imagePath";
import InputField from "./InputField";
import "./Form.css";
import ButtonField from "./ButtonField";

interface FieldDataItem {
  id?: number;
  maindiv?: string;
  classname?: string;
  labelclass?: string;
  label?: string;
  title?: string;
}

interface FormProps {
  printTitle?: {
    first: {
      heading: string;
      profile?: boolean;
      selection?: boolean;
      uploadItem?: boolean;
    }[];
  }[];
  heading?: string;
  body?: {
    genralFeild?: FieldDataItem[];
    contactFeild?: FieldDataItem[];
    salaryFeild?: FieldDataItem[];
  };
  handleInputChange?: (title: string) => (event: React.ChangeEvent<HTMLInputElement>) => void;
  handleFileChange?: (formDataevent: React.ChangeEvent<HTMLInputElement>) => void;
}

const Form: React.FC<FormProps> = ({ body, printTitle, heading, handleFileChange, handleInputChange }) => {
  return (
    <div
      className="modal modal-xl"
      id="exampleModal"
      aria-labelledby="exampleModalLabel"
      aria-hidden="false"
    >
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">
              {heading}
            </h5>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close"
            ></button>
          </div>

          {printTitle && printTitle[0].first.map((items) => (
            <div className="modal-body d-flex flex-column" key={items.heading}>
              <div className="p-2">
                <h5 className="fontW500_blackbd">{items.heading}</h5>
                {items.profile && (
                  <div className="d-flex gap-4">
                    <img src={imagePath.profile} alt="Profile" />
                    <div className="d-flex flex-row justify-content-center align-items-center">
                      <input
                        type="file"
                        accept="image/*"
                        onChange={handleFileChange}
                        style={{ display: "none" }}
                        id="imagePickerInput"
                      />
                      <label
                        htmlFor="imagePickerInput"
                        className="btn btn-warning text-white"
                      >
                        Upload
                      </label>
                    </div>
                  </div>
                )}

                <div className="d-flex flex-wrap p-2 gap-3">
                  {items.heading === "General Information" && body?.genralFeild?.map((item) => (
                    <InputField
                      key={item.id}
                      maindiv={item.maindiv}
                      classname={item.classname}
                      labelclass={item.labelclass}
                      label={item.label}
                      onChange={handleInputChange ? handleInputChange(item.title!) : undefined}
                    />
                  ))}
                  {items.heading === "Employee Information" && body?.contactFeild?.map((item) => (
                    <InputField
                      key={item.id}
                      maindiv={item.maindiv}
                      classname={item.classname}
                      labelclass={item.labelclass}
                      label={item.label}
                      onChange={handleInputChange ? handleInputChange(item.title!) : undefined}
                    />
                  ))}
                  {items.heading === "Salary Information" && body?.salaryFeild?.map((item) => (
                    <InputField
                      key={item.id}
                      maindiv={item.maindiv}
                      classname={item.classname}
                      labelclass={item.labelclass}
                      label={item.label}
                      onChange={handleInputChange ? handleInputChange(item.title!) : undefined}
                    />
                  ))}
                  {items.selection && (
                    <div className="d-flex flex-column w-25">
                      <label className="fontW500_black" id="cars">
                        Job Title
                      </label>
                      <select
                        className="inputSize200 ps-2"
                        id="cars"
                        name="carlist"
                        form="carform"
                      >
                        <option value="volvo"></option>
                        <option value="volvo">Volvo</option>
                        <option value="saab">Saab</option>
                        <option value="opel">Opel</option>
                        <option value="audi">Audi</option>
                      </select>
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-outline-warning"
              data-bs-dismiss="modal"
            >
              Close
            </button>
            <ButtonField
              type="button"
              classname="btn text-white btn-warning "
              label="Save changes"
              data_bs_dismiss="modal"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Form;
