import React, { useState } from "react";
import imagePath from "../constants/imagePath";
import InputField from "./InputField";
import "./Form.css";
import ButtonField from "./ButtonField";

const AddEmployeeForm = () => {
  const [employeeData, setEmployeeData] = useState({
    eid: "",
    firstname: "",
    lastname: "",
    language: "",
    email: "",
    address: "",
    phone: "",
    dob: "",
    doj: "",
    department: "",
    jobtitle: "",
    profile: "",
    resume: "",
  });

  // Function to handle input change
  const handleInputChange = (field:any) => (event:any) => {
    const { value } = event.target;
    setEmployeeData((prevData) => ({
      ...prevData,
      [field]: value,
    }));
  };

  // Function to handle profile change
  const handleProfileChange = (event:any) => {
    const file = event.target.files[0];
    setEmployeeData((prevData) => ({
      ...prevData,
      profile: file,
    }));
  };

  // Function to handle resume change
  const handleResumeChange = (event:any) => {
    const file = event.target.files[0];
    setEmployeeData((prevData) => ({
      ...prevData,
      resume: file,
    }));
  };
  const onSave = () => {
    console.log("Employee Data:", employeeData);
  };

  return (
    <div className="modal modal-xl" id="exampleModal" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">Add Employee</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div className="modal-body d-flex flex-column">
            <div className="p-2">
              <h5 className="fontW500_blackbd">General Information</h5>
              <div className="d-flex gap-4">
                <img src={imagePath.profile} alt="Profile" />
                <div className="d-flex flex-row justify-content-center align-items-center">
                  <input
                    type="file"
                    accept=".png, .jpg, .svg .jpeg"
                    onChange={handleProfileChange}
                    style={{ display: "none" }}
                    id="imagePickerInput"
                  />
                  <label htmlFor="imagePickerInput" className="btn btn-warning text-white">Upload</label>
                </div>
              </div>
              <div className="d-flex flex-wrap p-2 gap-3">
                <InputField
                  text={"text"}
                  labelclass="fontW500_black"
                  classname="inputSize200"
                  label="Employee Id"
                  value={employeeData.eid}
                  onChange={handleInputChange("eid")}
                />
                <InputField
                  text={"text"}
                  labelclass="fontW500_black"
                  classname="inputSize200"
                  label="First Name"
                  value={employeeData.firstname}
                  onChange={handleInputChange("firstname")}
                />
                <InputField
                  text={"text"}
                  labelclass="fontW500_black"
                  classname="inputSize200"
                  label="Last Name"
                  value={employeeData.lastname}
                  onChange={handleInputChange("lastname")}
                />
                <div className="d-flex flex-column w-25">
                  <label className="fontW500_black" htmlFor="language">
                    Language
                  </label>
                  <select
                    className="inputSize200 fontW500_black ps-2"
                    id="language"
                    name="language"
                    form="languageForm"
                    value={employeeData.language}
                    onChange={handleInputChange("language")}
                  >
                    <option value="Hindi">Hindi</option>
                    <option value="English">English</option>
                    <option value="French">French</option>
                    <option value="Marathi">Marathi</option>
                    <option value="Urdu">Urdu</option>
                    <option value="Punjabi">Punjabi</option>
                  </select>
                </div>
              </div>
            </div>
            <h5 className="fontW500_blackbd">Contact Information</h5>
            <div className="d-flex flex-wrap p-2 gap-3">
              <InputField
                text={"text"}
                labelclass="fontW500_black"
                classname="inputSize200"
                label="Address"
                value={employeeData.address}
                onChange={handleInputChange("address")}
              />
              <InputField
                text={"text"}
                labelclass="fontW500_black"
                classname="inputSize200"
                label="Phone"
                value={employeeData.phone}
                onChange={handleInputChange("phone")}
              />
            </div>
            <h5 className="fontW500_blackbd">Employee Information</h5>
            <div className="d-flex flex-wrap p-2 gap-3">
              <InputField
                text={"date"}
                labelclass="fontW500_black"
                classname="inputSize200"
                label="Date Of Joining"
                value={employeeData.doj}
                onChange={handleInputChange("doj")}
              />
              <div className="d-flex flex-column w-25">
                <label className="fontW500_black" htmlFor="jobTitle">Job Title</label>
                <select
                  className="inputSize200 fontW500_black ps-2"
                  id="jobTitle"
                  name="jobtitle"
                  form="jobTitleForm"
                  value={employeeData.jobtitle}
                  onChange={handleInputChange("jobtitle")}
                >
                  <option value="volvo">Volvo</option>
                  <option value="saab">Saab</option>
                  <option value="opel">Opel</option>
                  <option value="audi">Audi</option>
                </select>
              </div>
              <InputField
                text={"text"}
                labelclass="fontW500_black"
                classname="inputSize200"
                label="Department"
                value={employeeData.department}
                onChange={handleInputChange("department")}
              />
            </div>
            <div className="d-flex py-3 gap-5">
              <div className="d-flex flex-row justify-content-center align-items-center">
                <input
                  type="file"
                  accept=".pdf, .docx, .doc"
                  onChange={handleResumeChange}
                  style={{ display: "none" }}
                  id="resumePickerInput"
                />
                <label
                  htmlFor="resumePickerInput"
                  className="btn btn-warning text-white"
                >
                  Upload Resume
                </label>
              </div>
            </div>
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-outline-warning"
              data-bs-dismiss="modal"
            >
              Close
            </button>
            <ButtonField
              type="button"
              classname="btn text-white btn-warning"
              label="Save changes"
              data_bs_dismiss="modal"
              onClick={onSave}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddEmployeeForm;
