import React from 'react';
import { useNavigate } from 'react-router-dom';

interface AccordionItemProps {
  photo?: string;
  name?: string;
  gmail?: string;
  technology?: string;
  education?: string;
  educationYear?: string;
  skills?: string;
  experience?: string;
  recentEmployee?: string;
  portfolioLink?: string;
}

interface AccordionProps {
  data: AccordionItemProps[];
}

const Accordion: React.FC<AccordionProps> = ({ data }) => {
  const navigate = useNavigate();

  return (
    <div className="accordion" id="accordionExample">
      {data.map((item, index) => (
        <div className="accordion-item" key={index}>
          <h2 className="accordion-header" id={`heading${index}`}>
            <button
              className={`accordion-button ${index === 0 ? '' : 'collapsed'}`}
              type="button"
              data-bs-toggle="collapse"
              data-bs-target={`#collapse${index}`}
              aria-expanded={index === 0 ? 'true' : 'false'}
              aria-controls={`collapse${index}`}
            >
              <span className="arrow-icon"></span>
              <div className="row">
                <div className="col">{item.name}</div>
                <div className="col">{item.gmail}</div>
                <div className="col">{item.technology}</div>
                <div className="col">{item.educationYear}</div>
              </div>
            </button>
          </h2>
          <div
            id={`collapse${index}`}
            className={`accordion-collapse collapse ${index === 0 ? 'show' : ''}`}
            aria-labelledby={`heading${index}`}
            data-bs-parent="#accordionExample"
          >
            <div className="accordion-body">
              <strong>{item.name}'s Details</strong> - This section contains more detailed information about {item.name}. You can customize this content as needed.
              <div className="mt-2">
                <button
                  className="btn btn-primary"
                  onClick={() => navigate(`/profile/${item.name}`)}
                >
                  View Profile
                </button>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Accordion;
