import React, { useState } from "react";
import ButtonField from "../../components/ButtonField";
import Form from "../../components/Form";
import axios from "axios";
import { toast } from "react-toastify";
const Newemployee = () => {
  const printTitle = [
    {
      first: [
        {
          id: 1,
          profile: true,
          uploadItem: false,
          selection: false,
          heading: 'General Information'
        },
        {
          id: 2,
          profile: false,
          selection: false,
          heading: 'Contact Information',
          uploadItem: false
        },
        {
          id: 3,
          selection: true,
          profile: false,
          uploadItem: true,
          heading: 'Employee Information'
        }
      ]
    }
  ];
  const body = [
    {
      genralFeild: [
        {
          id: 1,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Employee Id",
          title: "employeeId",
        },
        {
          id: 2,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "First Name",
          title: "firstName",
        },
        {
          id: 3,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Last Name",
          title: "lastName",
        },
        {
          id: 4,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Date Of Birth",
          title: "dob",
        },
        {
          id: 5,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Language",
          title: "language",
        },
        {
          id: 6,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Email",
          title: "email",
        }
      ],
      contactFeild: [
        {
          id: 1,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Address",
          title: "address",
        },
        {
          id: 2,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Phone",
          title: "phone",
        }
      ],
      salaryFeild: [
        {
          id: 1,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Date Of Joining",
          title: "doj",
        },
        {
          id: 2,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Department",
          title: "department"
        }
      ]
    }
  ];
  const [hidden, setHidden] = useState(true);
  const [employeeData, setemployeeData] = useState({
    employeeId: "",
    firstName: "",
    lastName: "",
    language: "",
    email: '',
    address: "",
    phone: "",
    dob: "",
    doj: '',
    department: "",
    jobTitle: "",
    profile: "",
    resume: ""
  });

  const handleProfileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setemployeeData({ ...employeeData, profile: e.target.value });
  };
  const handleResumeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setemployeeData({ ...employeeData, resume: e.target.value });
  };
  const handleInputChange = (field: keyof typeof employeeData) => (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setemployeeData({ ...employeeData, [field]: e.target.value });
  };

  const handleSave = async (e:any) => {
    e.preventDefault();
    console.log("Form Data:", employeeData);
    let Data = new FormData();
    Data.append("employeeId", employeeData.employeeId);
    Data.append("firstname", employeeData.firstName);
    Data.append("lastname", employeeData.lastName);
    Data.append("phone", employeeData.phone);
    Data.append("jobtilte", employeeData.jobTitle);
    Data.append("email", employeeData.email);
    Data.append("language", employeeData.language)
    Data.append("department",  employeeData.department);
    Data.append("address", employeeData.address);
    Data.append("dob", employeeData.dob);
    Data.append("doj", employeeData.doj);
    Data.append("image", employeeData.profile);
    Data.append("resume", employeeData.resume);

      try {
        const response = await axios.post('http://localhost:8080/add',{Data});
       toast.success("Signup Successful");
        console.log(response);

      } catch (error) {
        toast.error("Signup Failed");
        console.error("There was an error signing up!", error);
      }

  }
  return (
    <>
      <div className="emp-main p-5">
        <ButtonField
          onClick={() => {
            setHidden(hidden ? false : true);
          }}
          data_bs_target="#exampleModal"
          type="button"
          iconleftside={"+"}
          classname="btn btn-warning text-white"
          data_bs_toggle="modal"
          label="Add New"
        />
        <Form
          body={body[0]}
          heading='Add employeeData'
          printTitle={printTitle}
          handleInputChange={handleInputChange}
          handleProfileChange={handleProfileChange}
          handleResumeChange={handleResumeChange}
          onSave={handleSave}
        />
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Name</th>
              <th scope="col">Eid</th>
              <th scope="col">Position</th>
              <th scope="col">Date Of Joining</th>
              <th scope="col">Salary</th>
              <th scope="col">Email Id</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
            </tr>
            <tr>
              <th scope="row">2</th>
              <td>Jacob</td>
              <td>Thornton</td>
              <td>@fat</td>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
            </tr>
            <tr>
              <th scope="row">3</th>
              <td>Larry</td>
              <td>the Bird</td>
              <td>@twitter</td>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
