import { useState } from 'react';
import Slidebar from '../components/Slidebar';
import Header from '../components/Header';
import imagePath from '../constants/imagePath';
import Profile from './Profile';
import { Outlet } from 'react-router-dom';
import { ToastContainer,toast } from 'react-toastify';

interface ActiveState {
  paris?: boolean;
  london?: boolean;
}
const MainComponent = () => {
  const [hiddenSidebar, setHiddenSidebar] = useState(true)
  const [sidebarOpen, setIsSidebarOpen] = useState(false);
  const [active, setActive] = useState<ActiveState>({ paris: false, london: true });
  const [headerLabel, setHeaderLabel] = useState('Dashboard');
  console.log(hiddenSidebar);
  const toggleSidebar = () => {
    setIsSidebarOpen(prevState => !prevState);
  };
  const body = [
    {
      id: 1,
      img: `${imagePath.uiuximg}`,
      heading: "UI/UX Designer ",
      paragraph: 'Investigation user experience design requirement for our suite of digital assets. Knowledge of colors, typography, and design.......',
      label: 'View More'
    },
    {
      id: 2,
      img: `${imagePath.reactimg}`,
      heading: "React Native Developer",
      paragraph: 'Investigation user experience design requirement for our suite of digital assets. Knowledge of colors, typography, and design.......',
      label: 'View More'
    },
    {
      id: 3,
      img: `${imagePath.nodeimg}`,
      heading: "Node Developer ",
      paragraph: 'Investigation user experience design requirement for our suite of digital assets. Knowledge of colors, typography, and design.......',
      label: 'View More'
    },
    {
      id: 4,
      img: `${imagePath.wordpressimg}`,
      heading: "WordPress Developer ",
      paragraph: 'Investigation user experience design requirement for our suite of digital assets. Knowledge of colors, typography, and design.......',
      label: 'View More'
    },
    {
      id: 5,
      img: `${imagePath.phpimg}`,
      heading: "PHP Developer ",
      paragraph: 'Investigation user experience design requirement for our suite of digital assets. Knowledge of colors, typography, and design.......',
      label: 'View More'
    },
    {
      id: 6,
      img: `${imagePath.drupalimg}`,
      heading: "Drupal Developer ",
      paragraph: 'Investigation user experience design requirement for our suite of digital assets. Knowledge of colors, typography, and design.......',
      label: 'View More'
    }
  ]; 

  return <>
  <ToastContainer/>
    {hiddenSidebar && <Slidebar isSidebarOpen={sidebarOpen} setHeaderLabel={setHeaderLabel} />}
    {hiddenSidebar && <section className="home-section">
      <Header toggleSidebar={toggleSidebar} label={headerLabel} setHiddenSidebar={setHiddenSidebar}  />
        <Outlet/> 
    </section>}
    {!hiddenSidebar && <Profile active={active} setactive={setActive} setHiddenSidebar={setHiddenSidebar}/>} 
   {/* <Demo/> */}
    {/* <Calendar/> */}
  </>
};

export default MainComponent;
