import React, { useState } from 'react';
import imagePath from '../constants/imagePath';
import colors from '../style/colors';
import ButtonField from './ButtonField';
import { Navigate, useNavigate } from 'react-router-dom';
interface Accordion {
    photo?:string;
    name?:string;
    gmail?:string;
    technology?:string;
    education?:string;
    educationYear?:string;
    skills?:string;
    experience?:string;
    recentEmployee?:string;
    portfolioLink?:string;
}
interface AccordionProps{
    data?:Accordion[];
}
const Accordion = ({data}:AccordionProps) => {
    const navigate = useNavigate();
    return (
        <>
            {data&&<div className="accordion" id="accordionExample">
                <div className="accordion-item">
                    <h2 className="accordion-header" id="headingOne">
                        <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
                            <span className="arrow-icon"></span><th scope="row"><i className='bx bxs-chevron-right arrow'/></th>
                                <td>{data[0]?.name}</td>
                                <td>{data[0]?.gmail}</td>
                                <td>{data[0]?.technology}</td>
                                <td>{data[0]?.educationYear}</td>          
                        </button>
                    </h2>
                    <div id="collapseOne" className="accordion-collapse collapse show" aria-labelledby="headingOne" data-bs-parent="#accordionExample">
                        <div className="accordion-body">
                            <strong>This is the first item's accordion body.</strong> It is shown by default, until the collapse plugin adds the appropriate classNamees that we use to style each element. These classNamees control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables. It's also worth noting that just about any HTML can go within the <code>.accordion-body</code>, though the transition does limit overflow.
                        </div>
                    </div>
                </div>
            </div>}
        </>
    );

};

export default Accordion;
