import React, { useEffect, useState } from "react";
import "./attendance.css";
import ButtonField from "../../components/ButtonField";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import axios from "axios";

const AttendanceSheet = () => {
  const [attendance, setAttendance] = useState([]);
  const [startDate, setStartDate] = useState(new Date());
  const [isAbsent, setIsAbsent] = useState(null);
  const [allEmployees, setAllEmployees] = useState([]);
  const [allAttendanceData, setAllAttendanceData] = useState([]);
  const options = { day: "numeric", month: "long", year: "numeric" };
  const today = new Date();
  const currentYear = today.getFullYear();
  const maxDate = new Date(currentYear, today.getMonth() + 1, 0);
  const formattedDate = today.toLocaleDateString("en-US", options);
  const currentDate = today.toLocaleDateString("en-GB", { day: "numeric", month: "long" });
  const monthYearString = new Date(startDate).toLocaleString("default", { month: "long" });
  const numberOfDaysInMonth = new Date(startDate.getFullYear(), startDate.getMonth() + 1, 0).getDate();

  const fetchAllAttendanceData = async () => {
    try {
      const response = await fetch("http://localhost:8080/getallattendance");
      const result = await response.json();
      setAllAttendanceData(result.data);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  const fetchAllEmployees = async () => {
    try {
      const response = await fetch("http://localhost:8080/getAllEmployee");
      const result = await response.json();
      setAllEmployees(result);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  const fetchCurrentDateData = async () => {
    try {
      const response = await fetch(`http://localhost:8080/getCurrentDateAttendance?date=${currentDate}`);
      const result = await response.json();
      if (result.status === 200) {
        setAttendance(result.data);
      }
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  useEffect(() => {
    fetchAllAttendanceData();
    fetchCurrentDateData();
    fetchAllEmployees();
  }, [currentDate]);

  const isPastDate = (dateString:string) => {
    const inputDate = new Date(`${dateString} ${currentYear}`);
    inputDate.setHours(0, 0, 0, 0);
    today.setHours(0, 0, 0, 0);
    return inputDate <= today;
  };

  const setAttendanceData = async (attendanceRecord:any) => {
    try {
      await axios.post("http://localhost:8080/addAttendance", attendanceRecord);
      fetchCurrentDateData(); // Refresh the data
    } catch (error) {
      console.error("Error setting attendance data: ", error);
    }
  };

  const updateAttendance = (eid:any, attendanceStatus:any, status:any) => {
    const updatedAttendance = attendance.map((att:any) =>
      att.eid === eid && att.date === currentDate
        ? { ...att, attendance: attendanceStatus, status }
        : att
    );

    const existingAttendance = attendance.find(
      (att:any) => att.eid === eid && att.date === currentDate
    );

    if (attendanceStatus === "absent") {
      setAttendanceData({ eid, attendance: attendanceStatus, date: currentDate, status });
      setIsAbsent(eid);
    } else {
      setIsAbsent(null);
    }

    if (!existingAttendance) {
      setAttendance([...attendance, { eid, attendance: attendanceStatus, date: currentDate, status }]);
    } else {
      setAttendance(updatedAttendance);
      setAttendanceData({ eid, attendance: attendanceStatus, date: currentDate, status });
    }
  };

  const monthDays = Array.from(
    { length: numberOfDaysInMonth },
    (_, i) => `${i + 1} ${monthYearString}`
  );

  return (
    <div className="d-flex p-3 flex-column">
      <div className="d-flex flex-row mb-3">
        <div className="w-75">
          <p>{formattedDate}</p>
        </div>
        <div className="center" style={{ width: "220px" }}>
          <DatePicker
            selected={startDate}
            onChange={(date:any) => setStartDate(date)}
            dateFormat="MMMM yyyy"
            showMonthYearPicker
            monthsShown={1}
            className="form-control"
            maxDate={maxDate}
          />
        </div>
      </div>
      <div className="w-100" style={{ overflowX: formattedDate !== currentDate ? "auto" : "clip" }}>
        <table className="table table-responsive">
          <thead>
            <tr>
              <th style={{ minWidth: "140px" }}>Eid</th>
              <th style={{ minWidth: "140px" }}>Name</th>
              <th style={{ minWidth: "140px" }}>D.O.B</th>
              <th style={{ minWidth: "140px" }}>D.O.J</th>
              {formattedDate === currentDate ? (
                <th style={{ minWidth: "140px" }}>Attendance</th>
              ) : (
                monthDays.map((day:any) =>
                  isPastDate(day) ? (
                    <th style={{ minWidth: "140px" }} key={day}>
                      {day}
                    </th>
                  ) : null
                )
              )}
            </tr>
          </thead>
          <tbody>
            {allEmployees.user &&
              allEmployees.user.map((item:any) => (
                <tr key={item.eid}>
                  <td style={{ minWidth: "140px" }}>{item.eid}</td>
                  <td style={{ minWidth: "140px" }}>{item.firstname}</td>
                  <td style={{ minWidth: "140px" }}>{item.dob}</td>
                  <td style={{ minWidth: "140px" }}>{item.doj}</td>
                  {formattedDate === currentDate ? (
                    <td className="width">
                      {attendance.some(
                        (att:any) => att.eid === item._id && att.date === currentDate
                      ) ? (
                        <span
                          className={
                            attendance.find(
                              (att:any) => att.eid === item._id && att.date === currentDate
                            )?.attendance === "present"
                              ? "text-success"
                              : "text-danger"
                          }
                        >
                          {attendance.find(
                            (att) => att.eid === item._id && att.date === currentDate
                          )?.attendance}
                        </span>
                      ) : (
                        <div>
                          <ButtonField
                            label="Absent"
                            onClick={() => updateAttendance(item._id, "absent", "false")}
                            classname={"btn btn-outline-danger "}
                          />
                          <ButtonField
                            label="Present"
                            onClick={() => updateAttendance(item._id, "present", "")}
                            classname={"btn btn-success ms-2"}
                          />
                        </div>
                      )}
                    </td>
                  ) : (
                    allAttendanceData.map((data) =>
                      monthDays.map((day) =>
                        data.date === day && item._id === data.eid ? (
                          <td
                            className={`width ${
                              data.status === "full day"
                                ? 'text-success'
                                : data.status === "half day"
                                ? 'text-warning'
                                : data.status === "early day"
                                ? 'text-primary'
                                : 'text-danger'
                            }`}
                            key={day}
                          >
                            {data.attendance === "present" ? 'P' : 'A'}
                          </td>
                        ) : null
                      )
                    )
                  )}
                  {formattedDate === currentDate && (
                    <td className="text-center">
                      <div className="dropdown">
                        <button className="dropbtn btn btn-light">
                          <i className="fa fa-ellipsis-v"></i>
                        </button>
                        <div className="dropdown-content">
                          <ButtonField
                            classname={"bg-light text-dark"}
                            onClick={() => updateAttendance(item._id, "present", "full day")}
                            label="Full Day"
                            disabled={isAbsent === item._id}
                          />
                          <ButtonField
                            classname={"bg-light text-dark my-1"}
                            onClick={() => updateAttendance(item._id, "present", "half day")}
                            label="Half Day"
                            disabled={isAbsent === item._id}
                          />
                          <ButtonField
                            classname={"bg-light text-dark "}
                            onClick={() => updateAttendance(item._id, "present", "early day")}
                            label="Early Leave"
                            disabled={isAbsent === item._id}
                          />
                        </div>
                      </div>
                    </td>
                  )}
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AttendanceSheet;
