import React, { useEffect, useState } from "react";
import "./attendence.css";
import ButtonField from "../../components/ButtonField";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import axios from "axios";

const AttendanceSheet = () => {
  const [attendance, setAttendance] = useState<any>();
  const [startDate, setStartDate] = useState(new Date());
  const [isAbsent, setIsAbsent] = useState<string | null>(null);
  const today = new Date();
  const currentYear = today.getFullYear();
  const currentMonth = today.getMonth();
  const maxDate = new Date(currentYear, currentMonth + 1, 0);
  const options: any = { day: "numeric", month: "long", year: "numeric" };
  const options1: any = { day: "numeric", month: "long" };
  const currentDate = today.toLocaleDateString("en-US", options1);
  const date1 = new Date(startDate);
  const date = new Date(startDate).toLocaleDateString("en-US", options);
  const formattedDate = today.toLocaleDateString("en-US", options);
  const monthYearString = date1.toLocaleString("default", { month: "long" });
  const [allEmployees, setAllEmployees] = useState<any>([]);
  const numberOfDaysInMonth = new Date(
    date1.getFullYear(),
    date1.getMonth() + 1,
    0
  ).getDate();

  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:8080/getAllEmployee");
      const result = await response.json();
      console.log(result);
      setAllEmployees(result);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const monthDays = Array.from(
    { length: numberOfDaysInMonth },
    (_, i) => `${i + 1} ${monthYearString}`
  );

  const isPastDate = (dateString: string) => {
    const today = new Date();
    const currentYear = today.getFullYear();

    const inputDate = new Date(`${dateString} ${currentYear}`);

    today.setHours(0, 0, 0, 0);
    inputDate.setHours(0, 0, 0, 0);

    return inputDate <= today;
  };

  const setAttendanceData = async (eid: string,
    date: string,
    status: string,
    attendance: string,) => {

    try {


      let response = await axios.post("http://localhost:8080/addAttendance", {date,status,att});
      console.log(response);
    } catch (error) {
      console.log(error);
    }
  }

  // const setAttendance1 = (
  //   eid: string,
  //   attendanceStatus: string,
  //   status: string
  // ) => {
  //   setAttendance((prevAttendance: any) => {
  //     const existingAttendance = prevAttendance.find(
  //       (att: any) => att.eid === eid && att.date === currentDate
  //     );

  //     if (attendanceStatus === "Absent") {
  //       console.log(eid+"     "+attendanceStatus+"   "+currentDate);

  //       setAttendanceData({eid,attendanceStatus,currentDate});
  //       setIsAbsent(eid);
  //     } else {
  //       setIsAbsent(null);
  //     }

  //     if (existingAttendance) {
  //       return prevAttendance.map((att: any) =>
  //         att.eid === eid && att.date === currentDate
  //           ? { ...att, attendance: attendanceStatus, status: status }
  //           : att
  //       );
  //     } else {
  //       return [
  //         ...prevAttendance,
  //         {
  //           eid: eid,
  //           attendance: attendanceStatus,
  //           date: currentDate,
  //           status: status,
  //         },
  //       ];
  //     }
  //   });
  // };


  console.log(attendance);

  return (
    <>
      <div className="d-flex p-3 flex-column">
        <div className="d-flex flex-row mb-3">
          <div className="w-75">
            <p>{formattedDate}</p>
          </div>
          <div className="center" style={{ width: "220px" }}>
            <DatePicker
              selected={startDate}
              onChange={(date: any) => setStartDate(date)}
              dateFormat="MMMM yyyy"
              showMonthYearPicker
              monthsShown={1}
              className="form-control"
              maxDate={maxDate}
            />
          </div>
        </div>
        <div
          className="w-100"
          style={{ overflowX: formattedDate !== date ? "auto" : "clip" }}
        >
          <table className="table table-responsive">
            <thead>
              <tr>
                <th style={{ minWidth: "140px" }}>Eid</th>
                <th style={{ minWidth: "140px" }}>Name</th>
                <th style={{ minWidth: "140px" }}>D.O.B</th>
                <th style={{ minWidth: "140px" }}>D.O.J</th>
                {formattedDate === date ? (
                  <th style={{ minWidth: "140px" }}>Attendance</th>
                ) : (
                  monthDays.map((day: string) =>
                    isPastDate(day) ? (
                      <th style={{ minWidth: "140px" }} key={day}>
                        {day}
                      </th>
                    ) : null
                  )
                )}
              </tr>
            </thead>
            <tbody>
              {allEmployees.user &&
                allEmployees.user.map((item: any) => (
                  <tr key={item.eid}>
                    <th style={{ minWidth: "140px" }}>{item.eid}</th>
                    <th style={{ minWidth: "140px" }}>{item.firstname}</th>
                    <th style={{ minWidth: "140px" }}>{item.dob}</th>
                    <th style={{ minWidth: "140px" }}>{item.doj}</th>
                    {formattedDate === date ? (
                      <td className={`width`}>
                        {attendance.some(
                          (att: any) =>
                            att.eid === item._id && att.date === currentDate
                        ) ? (
                          <span
                            className={
                              attendance.find(
                                (att: any) =>
                                  att.eid === item._id &&
                                  att.date === currentDate
                              )?.attendance === "Present"
                                ? "text-success"
                                : "text-danger"
                            }
                          >
                            {
                              attendance.find(
                                (att: any) =>
                                  att.eid === item._id &&
                                  att.date === currentDate
                              )?.attendance
                            }
                          </span>
                        ) : (
                          <div>
                            <ButtonField
                              label="Absent"
                              onClick={() => {
                                setAttendance({
                                  eid: item._id,
                                  status: "Absent"
                                })
                              }}
                              classname={"btn btn-outline-danger "}
                            />
                            <ButtonField
                              label="Present"
                              onClick={() => {
                                setAttendance({
                                  eid: item._id,
                                  status: "Present"
                                })
                              }}
                              classname={"btn btn-success ms-2"}
                            />
                          </div>
                        )}
                      </td>
                    ) : (
                      <td className="width"></td>
                    )}
                    {formattedDate === date && (
                      <td className="text-center">
                        <div className="dropdown">
                          <button className="dropbtn btn btn-light">
                            <i className="fa fa-ellipsis-v"></i>
                          </button>
                          <div className="dropdown-content">
                            <ButtonField
                              classname={"bg-light text-dark"}
                              onClick={() =>
                                setAttendance1(item._id, "Present", "Full Day")
                              }
                              label="Full Day"
                              disabled={isAbsent === item._id}
                            />
                            <ButtonField
                              classname={"bg-light text-dark my-1"}
                              onClick={() =>
                                setAttendance1(item._id, "Present", "Half Day")
                              }
                              label="Half Day"
                              disabled={isAbsent === item._id}
                            />
                            <ButtonField
                              classname={"bg-light text-dark "}
                              onClick={() =>
                                setAttendance1(
                                  item._id,
                                  "Present",
                                  "Early Leave"
                                )
                              }
                              label="Early Leave"
                              disabled={isAbsent === item._id}
                            />
                          </div>
                        </div>
                      </td>
                    )}
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default AttendanceSheet;
