import React, { useState, useEffect } from "react";
import imagePath from "../constants/imagePath";
import InputField from "./InputField";
import "./Form.css";
import ButtonField from "./ButtonField";

interface FormProps {
  employeeData?: any;
  heading?: any;
  handleInputChange?: (field: any) => (event: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => void;
  handleProfileChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
  handleResumeChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
  onSave?: () => void;
}

const AddEmployeeForm: React.FC<FormProps> = ({
  heading,
  employeeData,
  handleProfileChange,
  handleResumeChange,
  handleInputChange,
  onSave
}) => {
  const [isValid, setIsValid] = useState(true);
  const [errors, setErrors] = useState<any>({});
  const [submitted, setSubmitted] = useState(false);

  // Function to check form validity and set error messages
  const validateForm = () => {
    const newErrors: any = {};
    const requiredFields = [
      { field: 'eid', label: 'Employee ID' },
      { field: 'firstname', label: 'First Name' },
      { field: 'lastname', label: 'Last Name' },
      { field: 'dob', label: 'Date of Birth' },
      { field: 'language', label: 'Language' },
      { field: 'email', label: 'Email' },
      { field: 'address', label: 'Address' },
      { field: 'phone', label: 'Phone' },
      { field: 'doj', label: 'Date Of Joining' },
      { field: 'jobtitle', label: 'Job Title' },
      { field: 'department', label: 'Department' }
    ];

    requiredFields.forEach(({ field, label }) => {
      if (!employeeData?.[field]?.trim()) {
        newErrors[field] = `${label} is required.`;
      }
    });

    setErrors(newErrors);
    setIsValid(Object.keys(newErrors).length === 0);
  };

  const handleSaveClick = () => {
    setSubmitted(true);
    validateForm();
    if (isValid && onSave) {
      onSave();
    }
  };

  useEffect(() => {
    // Validate the form whenever employeeData changes
    validateForm();
  }, [employeeData]);

  return (
    <div className="modal modal-xl" id="exampleModal" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">{heading}</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div className="modal-body d-flex flex-column">
            <div className="p-2">
              <h5 className="fontW500_blackbd">General Information</h5>
              <div className="d-flex gap-4">
                <img src={imagePath.profile} alt="Profile" />
                <div className="d-flex flex-row justify-content-center align-items-center">
                  <input
                    type="file"
                    accept=".png, .jpg, .svg .jpeg"
                    onChange={handleProfileChange}
                    style={{ display: "none" }}
                    id="imagePickerInput"
                  />
                  <label htmlFor="imagePickerInput" className="btn btn-warning text-white">Upload</label>
                </div>
              </div>
              <div className="d-flex flex-wrap p-2 gap-3">
                <div className="d-flex flex-column">
                  <label className="fontW500_black" htmlFor="eid">Employee Id</label>
                  <InputField
                    text={"text"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    id="eid"
                    readOnly={heading === 'Update Employee' ? true : false}
                    value={employeeData.eid}
                    onChange={handleInputChange ? handleInputChange("eid") : undefined}
                  />
                  {submitted && errors.eid && <div className="text-danger">{errors.eid}</div>}
                </div>
                <div className="d-flex flex-column">
                  <label className="fontW500_black" htmlFor="firstname">First Name</label>
                  <InputField
                    text={"text"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    id="firstname"
                    value={employeeData.firstname}
                    onChange={handleInputChange ? handleInputChange("firstname") : undefined}
                  />
                  {submitted && errors.firstname && <div className="text-danger">{errors.firstname}</div>}
                </div>
                <div className="d-flex flex-column">
                  <label className="fontW500_black" htmlFor="lastname">Last Name</label>
                  <InputField
                    text={"text"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    id="lastname"
                    value={employeeData.lastname}
                    onChange={handleInputChange ? handleInputChange("lastname") : undefined}
                  />
                  {submitted && errors.lastname && <div className="text-danger">{errors.lastname}</div>}
                </div>
                <div className="d-flex flex-column">
                  <label className="fontW500_black" htmlFor="dob">Date of Birth</label>
                  <InputField
                    text={"date"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    id="dob"
                    value={employeeData.dob}
                    onChange={handleInputChange ? handleInputChange("dob") : undefined}
                  />
                  {submitted && errors.dob && <div className="text-danger">{errors.dob}</div>}
                </div>
                <div className="d-flex flex-column">
                  <label className="fontW500_black" htmlFor="language">Language</label>
                  <select
                    className="inputSize200 fontW500_black ps-2"
                    id="language"
                    name="language"
                    form="languageForm"
                    value={employeeData.language}
                    onChange={handleInputChange ? handleInputChange("language") : undefined}
                  >
                    <option value="Hindi">Hindi</option>
                    <option value="English">English</option>
                    <option value="French">French</option>
                    <option value="Marathi">Marathi</option>
                    <option value="Urdu">Urdu</option>
                    <option value="Punjabi">Punjabi</option>
                  </select>
                  {submitted && errors.language && <div className="text-danger">{errors.language}</div>}
                </div>
                <div className="d-flex flex-column">
                  <label className="fontW500_black" htmlFor="email">Email</label>
                  <InputField
                    text={"text"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    id="email"
                    value={employeeData.email}
                    onChange={handleInputChange ? handleInputChange("email") : undefined}
                  />
                  {submitted && errors.email && <div className="text-danger">{errors.email}</div>}
                </div>
              </div>
            </div>
            <h5 className="fontW500_blackbd">Contact Information</h5>
            <div className="d-flex flex-wrap p-2 gap-3">
              <div className="d-flex flex-column">
                <label className="fontW500_black" htmlFor="address">Address</label>
                <InputField
                  text={"text"}
                  labelclass="fontW500_black"
