import React from 'react';
import imagePath from '../constants/imagePath';
import colors from '../style/colors';
import ButtonField from './ButtonField';
import { useNavigate } from 'react-router-dom';

interface Accordion {
  photo?: string;
  name?: string;
  gmail?: string;
  technology?: string;
  education?: string;
  educationYear?: string;
  skills?: string;
  experience?: string;
  recentEmployee?: string;
  portfolioLink?: string;
}

interface AccordionProps {
  data?: Accordion[];
}

const Accordion = ({ data }: AccordionProps) => {
  const navigate = useNavigate();

  return (
    <>
      {data && (
        <div className="accordion" id="accordionExample">
          {data.map((item, index) => (
            <div key={index} className="accordion-item">
              <h2 className="" >
                  <span id={`heading${index}`} className="accordion-header arrow-icon"  data-bs-toggle="collapse"
                  data-bs-target={`#collapse${index}`}
                  aria-expanded="true"
                  aria-controls={`collapse${index}`}>

                                <td> <i className='bx bxs-chevron-right arrow' /></td>
                                <td>{data[0]?.name}</td>
                                <td>{data[0]?.gmail}</td>
                                <td>{data[0]?.technology}</td>
                                <td>{data[0]?.educationYear}</td>    
                  </span>

              </h2>
              <div
                id={`collapse${index}`}
                className="accordion-collapse collapse"
                aria-labelledby={`heading${index}`}
                data-bs-parent="#accordionExample"
              >
                <div className="accordion-body">
                  <div className="profile-section d-flex flex-row flex-wrap">
                    <div className="col-md-4 d-flex justify-content-center align-items-center border-right">
                      <img className="w-50 rounded-circle" src={imagePath.profilelogo} alt="Profile" />
                    </div>
                    <div className="col-md-8">
                      <div className='p-3'>
                        <div className="row mt-2">
                          <div className="col-md-8 col-sm-6 col-8">
                            <div>{item.name}</div>
                            <div><label>{item.skills}</label></div>
                          </div>
                          <div className="col-md-4 col-sm-6 col-4 text-right">
                            <a className="text-decoration-none d-flex justify-content-center align-items-center">
                              <i className="fa fa-edit" style={{ color: '#FEA633', maxHeight: '20px' }}>Edit</i>
                            </a>
                          </div>
                        </div>
                        {/* Add other profile details as needed */}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </>
  );
};

export default Accordion;
