import React, { useState } from "react";
import "react-circular-progressbar/dist/styles.css";
import Form from "../../components/Form";
import ButtonField from "../../components/ButtonField";



const Salary = () => {
  const [hidden, setHidden] = useState(true);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    status: "",
    salary: "",
    annualpay: "",
    jobTitle: "",
    files: "",
  });
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, files: e.target.value });
  };

  const handleInputChange =
    (field: keyof typeof formData) =>
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setFormData({ ...formData, [field]: e.target.value });
    };
  const printTitle = [ 
    {
       first:[
        {
            id: 1,
            profile:true,
            selection:false,
            heading:'General Information'
          },
          {
              id: 2,
              profile:false,
              selection:false,
              heading:'Employee Information'
          },
          {
              id: 3,
              selection:true,
              profile:false,
              heading:'Salary Information'
            }
       ]
    }
  ];
  const body = [
        {
            genralFeild:[
                {
                    id: 1,
                    maindiv: "maindiv",
                    classname: "inputSize200",
                    labelclass: "fontW500_black",
                    label: "Employee Id",
                    title: "employeeId",
                  },
                  {
                    id: 2,
                    maindiv: "maindiv",
                    classname: "inputSize200",
                    labelclass: "fontW500_black",
                    label: "First Name",
                    title: "firstName",
                  },
                  {
                    id: 3,
                    maindiv: "maindiv",
                    classname: "inputSize200",
                    labelclass: "fontW500_black",
                    label: "Last Name",
                    title: "lastName",
                  }
            ],
            contactFeild:[
                {
                    id: 1,
                    maindiv: "maindiv",
                    classname: "inputSize200",
                    labelclass: "fontW500_black",
                    label: "Employee Id",
                    title: "employeeId",
                  },
                  {
                    id: 2,
                    maindiv: "maindiv",
                    classname: "inputSize200",
                    labelclass: "fontW500_black",
                    label: "First Name",
                    title: "firstName",
                  }
            ],
            salaryFeild:[
                {
                    id: 1,
                    maindiv: "maindiv",
                    classname: "inputSize200",
                    labelclass: "fontW500_black",
                    label: "Employee Id",
                    title: "employeeId",
                  }
            ]
        }
  ];
  return (
    <>
      <ButtonField
        onClick={() => {
          setHidden(hidden ? false : true);
        }}
        data_bs_target="#exampleModal"
        type="button"
        iconleftside={"+"}
        classname="btn btn-warning text-white"
        data_bs_toggle="modal"
        label="Add Payroll"
      />
      <Form
        body={body[0]}
        heading='Add Salary'
        printTitle={printTitle}
      />
      <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Name</th>
                        <th scope="col">Eid</th>
                        <th scope="col">Position</th>
                        <th scope="col">Date Of Joining</th>
                        <th scope="col">Salary</th>
                        <th scope="col">Email Id</th>
                        <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>
                <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">3</th>
                        <td>Larry</td>
                        <td>the Bird</td>
                        <td>@twitter</td>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">3</th>
                        <td>Larry</td>
                        <td>the Bird</td>
                        <td>@twitter</td>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                </tbody>
            </table>
    </>
  );
};

export default Salary;
