import React, { useState } from "react";
import ButtonField from "../../components/ButtonField";
import Form from "../../components/Form";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";
import { log } from "console";

const Newemployee = () => {
  const printTitle = [
    {
      first: [
        {
          id: 1,
          profile: true,
          uploadItem: false,
          selection: false,
          heading: 'General Information'
        },
        {
          id: 2,
          profile: false,
          selection: false,
          heading: 'Contact Information',
          uploadItem: false
        },
        {
          id: 3,
          selection: true,
          profile: false,
          uploadItem: true,
          heading: 'Employee Information'
        }
      ]
    }
  ];
  const body = [
    {
      genralFeild: [
        {
          id: 1,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Employee Id",
          title: "employeeId",
        },
        {
          id: 2,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "First Name",
          title: "firstName",
        },
        {
          id: 3,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Last Name",
          title: "lastName",
        },
        {
          id: 4,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Date Of Birth",
          title: "dob",
        },
        {
          id: 5,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Language",
          title: "language",
        },
        {
          id: 6,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Email",
          title: "email",
        }
      ],
      contactFeild: [
        {
          id: 1,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Address",
          title: "address",
        },
        {
          id: 2,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Phone",
          title: "phone",
        }
      ],
      salaryFeild: [
        {
          id: 1,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Date Of Joining",
          title: "doj",
        },
        {
          id: 2,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Department",
          title: "department"
        }
      ]
    }
  ];
  const [hidden, setHidden] = useState(true);
  const [employeeData, setemployeeData] = useState<any>({
    eid: "",
    firstname: "",
    lastname: "",
    language: "",
    email: '',
    address: "",
    phone: "",
    dob: "",
    doj: '',
    department: "",
    jobtitle: "",
    profile:'',
    resume:''
  });
  console.log("Form Data:", employeeData);
  const handleProfileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setemployeeData({ ...employeeData, profile: e.target.files&&e.target.files[0] });
  };
  const handleResumeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    console.log("e.target.files",e.target.files)
    setemployeeData({ ...employeeData, resume: e.target.files&&e.target.files[0] });
  };
  const handleInputChange = (field: keyof typeof employeeData) => (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setemployeeData({ ...employeeData, [field]: e.target.value });
  };

  const handleSave = async () => {
    try {
      const formdata = new FormData();
      formdata.append("eid", "100");
      formdata.append("firstname", "dsafdsdsa");
      formdata.append("lastname", "dsafasds");
      formdata.append("language", "sdafds");
      formdata.append("address", "sdafsad");
      formdata.append("phone", "dsafsaf");
      formdata.append("dob", "dsfdsaf");
      formdata.append("doj", "dsafdsaf");
      formdata.append("department", "dssaf");
      formdata.append("jobtitle", "dsfdsaf");
      formdata.append("profile", fileInput.files[0], "Profilephoto.png");
      formdata.append("resume", fileInput.files[0], "Basic_Resume.pdf");



      fetch("http://localhost:8080/addEmployee",{method: "POST",
        body: formdata,
        redirect: "follow",})
        .then((response) => response.json())
        .then((result) => console.log(result))
        .catch((error) => console.error(error));
    } catch (error) {}  
      try {

        console.log("Form Data:", employeeData);
        let formData = new FormData();
        formData.append("eid", employeeData.eid);
        formData.append("firstname", employeeData.firstname);
        formData.append("lastname", employeeData.lastname);
