import React, { useState } from "react";
import ButtonField from "../../components/ButtonField";
import Form from "../../components/Form";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";
import { log } from "console";
import InputFeild from "../../components/InputField";

const Newemployee = () => {
  const printTitle = [
    {
      first: [
        {
          id: 1,
          profile: true,
          uploadItem: false,
          selection: false,
          heading: 'General Information'
        },
        {
          id: 2,
          profile: false,
          selection: false,
          heading: 'Contact Information',
          uploadItem: false
        },
        {
          id: 3,
          selection: true,
          profile: false,
          uploadItem: true,
          heading: 'Employee Information'
        }
      ]
    }
  ];
  const body = [
    {
      genralFeild: [
        {
          id: 1,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Employee Id",
          title: "employeeId",
        },
        {
          id: 2,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "First Name",
          title: "firstName",
        },
        {
          id: 3,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Last Name",
          title: "lastName",
        },
        {
          id: 4,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Date Of Birth",
          title: "dob",
        },
        {
          id: 5,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Language",
          title: "language",
        },
        {
          id: 6,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Email",
          title: "email",
        }
      ],
      contactFeild: [
        {
          id: 1,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Address",
          title: "address",
        },
        {
          id: 2,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Phone",
          title: "phone",
        }
      ],
      salaryFeild: [
        {
          id: 1,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Date Of Joining",
          title: "doj",
        },
        {
          id: 2,
          maindiv: "maindiv",
          classname: "inputSize200",
          labelclass: "fontW500_black",
          label: "Department",
          title: "department"
        }
      ]
    }
  ];
  const [hidden, setHidden] = useState(true);
  const [employeeData, setemployeeData] = useState<any>({
    eid: "",
    firstname: "",
    lastname: "",
    language: "",
    email: '',
    address: "",
    phone: "",
    dob: "",
    doj: '',
    department: "",
    jobtitle: "",
    profile:'',
    resume:''
  });
  console.log("Form Data:", employeeData);
  const handleProfileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setemployeeData({ ...employeeData, profile: e.target.files&&e.target.files[0] });
  };
  const handleResumeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    console.log("e.target.files",e.target.files)
    setemployeeData({ ...employeeData, resume: e.target.files&&e.target.files[0] });
  };
  const handleInputChange = (field: keyof typeof employeeData) => (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setemployeeData({ ...employeeData, [field]: e.target.value });
  };

  const handleSave = async () => {
    try {
      let formData = new FormData();
      formData.append("eid", employeeData.eid);
      formData.append("firstname", employeeData.firstname);
      formData.append("lastname", employeeData.lastname);
      formData.append("phone", employeeData.phone);
      formData.append("jobtitle", employeeData.jobtitle);
      formData.append("department", employeeData.department);
      formData.append("email", employeeData.email);
      formData.append("language", employeeData.language);
      formData.append("address", employeeData.address);
      formData.append("dob", employeeData.dob);
      formData.append("doj", employeeData.doj);
      formData.append("profile", employeeData.profile);
      formData.append("resume", employeeData.resume);


      fetch("http://localhost:8080/addEmployee",{method: "POST",
        body: formData,
        redirect: "follow",})
        .then((response) => response.json())
        .then((result) => console.log(result))
        .catch((error) => console.error(error));
    } catch (error) {}  


  }
  return (
    <>
    <ToastContainer/>
      <div className="emp-main p-5">
        <ButtonField
          onClick={() => {
            setHidden(hidden ? false : true);
          }}
          data_bs_target="#exampleModal"
          type="button"
          iconleftside={"+"}
          classname="btn btn-warning text-white"
          data_bs_toggle="modal"
          label="Add New"
        />
        <Form
          body={body[0]}
          heading='Add employeeData'
          printTitle={printTitle}
          handleInputChange={handleInputChange}
          handleProfileChange={handleProfileChange}
          handleResumeChange={handleResumeChange}
          onSave={handleSave}
        />
        <div className="d-flex p-3 flex-column">
        <div className="first d-flex flex-row">
          <div className="w-75">
            <p>1 September 2023</p>
          </div>
          <div className="w-25">
            <InputFeild classname="inputSize200" text={'date'} />
          </div>
        </div>
        <div className="second p-2  w-100">
          <table className="table">
            <thead>
              <tr>
                <th className="fixed-width">Eid</th>
                <th className="fixed-width">Name</th>
                <th className="fixed-width">D.O.B</th>
                <th className="fixed-width">D.O.J</th>
                <th className="expandable justify-content-end align-items-end d-flex">
                  Attendence
                </th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <th className="fixed-width" scope="row">
                  1
                </th>
                <td className="fixed-width">Mark</td>
                <td className="fixed-width">Otto</td>
                <td className="fixed-width">@mdo</td>
                <td
                  className={`expandable d-flex justify-content-end align-items-end " ${attendance === "Present" ? "text-success" : "text-danger"
                    }`}
                >
                  {hidden && (
                    <>
                      <ButtonField
                        label="Absent"
                        onClick={() => {
                          setAttendance("Absent")
                          setHidden(false);
                        }}
                        classname={"btn btn-outline-dark  me-2"}
                      />
                      <ButtonField
                        label="Present"
                        onClick={() => {
                          setAttendance("Present")
                          setHidden(false);
                        }}
                        classname={"btn btn-success"}
                      />
                    </>
                  )}
                  {attendance === "" ? "" : attendance}
                </td>
                <td className="align-content-center">
                  <div className="dropdown">
                    <button className="dropbtn btn btn-light"><i className="fa fa-ellipsis-v"></i></button>
                    <div className="dropdown-content">
                      <a href="/maincomponent/profile"><i className="fa fa-sun-o"></i>Half Day</a>
                      <a href="#"><i className="fa fa-sun-o"></i>Early Leave</a>
                    </div>
                  </div>

                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
      </div>
    </>
  );
};

export default Newemployee;
