import React, { useState } from 'react';
import imagePath from '../../constants/imagePath';
import InputFeild from '../../components/InputField';
import ButtonField from '../../components/ButtonField';
import colors from '../../style/colors';
import './loginPage.css';
import { useNavigate } from 'react-router-dom';
const SignUpPage = () => {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const handleClickButton = () => {
    alert("Hello GoodEvening " + name)
  }
  return (
    <form action="/#" method="post">
      <div className="main-container">
        <div className='left-container center'>
          <img className='abstract' src={imagePath.abstract}></img>
        </div>
        <div className='right-container ' >
          <div className='right-container-first center'>
            <img className='logo' src={imagePath.logo}></img>
          </div>
          <div className='right-container-second' >
            <div className='login-form'>
              <h4 className='fontS27' style={{ color: colors.yellow }}>Sign Up </h4>
              <InputFeild classname='inputSize364 ' requi labelclass='fontW500_black' label='Email Address' placeholder='Enter Full Name' onChange={(e) => { setName(e.target.value) }} />
              <InputFeild classname='inputSize364 ' labelclass='fontW500_black' label='Email Address' placeholder='Enter Email Address' onChange={(e) => { setEmail(e.target.value) }} />
              <InputFeild classname='inputSize364 ' labelclass='fontW500_black' label='Password' placeholder='Enter Password' onChange={(e) => { setPassword(e.target.value) }} />
              <InputFeild classname='inputSize364 ' labelclass='fontW500_black' label='Password' placeholder='Enter Passwor Again' onChange={(e) => { setPassword(e.target.value) }} />
              <ButtonField type='button' classname='buttonSize364_yellow' label='Sign Up' onClick={() => { navigate('/maincomponent') }} />
            </div>
          </div>

        </div>
      </div>
    </form>
  );

};

export default SignUpPage;
