import React, { useState } from "react";
import imagePath from "../constants/imagePath";
import InputField from "./InputField";
import "./Form.css";
import ButtonField from "./ButtonField";

interface FormProps {
  employeeData?: any;
  heading?: any;
  handleInputChange?: (field: any) => (event: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => void;
  handleProfileChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
  handleResumeChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
  onSave?: () => void;
}

const AddEmployeeForm: React.FC<FormProps> = ({
  heading,
  employeeData,
  handleProfileChange,
  handleResumeChange,
  handleInputChange,
  onSave
}) => {
  const [errors, setErrors] = useState<any>({});

  const validateEmail = (email: string) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validateForm = () => {
    const newErrors: any = {};
    if (!employeeData?.eid) newErrors.eid = "Employee ID is required.";
    if (!employeeData?.firstname) newErrors.firstname = "First Name is required.";
    if (!employeeData?.lastname) newErrors.lastname = "Last Name is required.";
    if (!employeeData?.dob) newErrors.dob = "Date of Birth is required.";
    if (!employeeData?.email) {
      newErrors.email = "Email is required.";
    } else if (!validateEmail(employeeData.email)) {
      newErrors.email = "Email format is invalid.";
    }
    if (!employeeData?.address) newErrors.address = "Address is required.";
    if (!employeeData?.phone) newErrors.phone = "Phone number is required.";
    if (!employeeData?.doj) newErrors.doj = "Date Of Joining is required.";
    if (!employeeData?.jobtitle) newErrors.jobtitle = "Job Title is required.";
    if (!employeeData?.department) newErrors.department = "Department is required.";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = () => {
    if (validateForm()) {
      if (onSave) onSave();
    }
  };

  return (
    <div className="modal modal-xl" id="exampleModal" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">{heading}</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div className="modal-body d-flex flex-column">
            <div className="p-2">
              <h5 className="fontW500_blackbd">General Information</h5>
              <div className="d-flex gap-4">
                <img src={imagePath.profile} alt="Profile" />
                <div className="d-flex flex-row justify-content-center align-items-center">
                  <input
                    type="file"
                    accept=".png, .jpg, .svg .jpeg"
                    onChange={handleProfileChange}
                    style={{ display: "none" }}
                    id="imagePickerInput"
                  />
                  <label htmlFor="imagePickerInput" className="btn btn-warning text-white">Upload</label>
                </div>
              </div>
              <div className="d-flex flex-wrap p-2 gap-3">
                <div>
                  <label className="fontW500_black">Employee Id</label>
                  <InputField
                    text={"text"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    value={employeeData.eid}
                    onChange={handleInputChange ? handleInputChange("eid") : undefined}
                    error={errors.eid}
                  />
                </div>
                <div>
                  <label className="fontW500_black">First Name</label>
                  <InputField
                    text={"text"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    value={employeeData.firstname}
                    onChange={handleInputChange ? handleInputChange("firstname") : undefined}
                    error={errors.firstname}
                  />
                </div>
                <div>
                  <label className="fontW500_black">Last Name</label>
                  <InputField
                    text={"text"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    value={employeeData.lastname}
                    onChange={handleInputChange ? handleInputChange("lastname") : undefined}
                    error={errors.lastname}
                  />
                </div>
                <div>
                  <label className="fontW500_black">Date of Birth</label>
                  <InputField
                    text={"date"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    value={employeeData.dob}
                    onChange={handleInputChange ? handleInputChange("dob") : undefined}
                    error={errors.dob}
                  />
                </div>
                <div className="d-flex flex-column w-25">
                  <label className="fontW500_black" htmlFor="language">
                    Language
                  </label>
                  <select
                    className="inputSize200 fontW500_black ps-2"
                    id="language"
                    name="language"
                    form="languageForm"
                    value={employeeData.language}
                    onChange={handleInputChange ? handleInputChange("language") : undefined}
                  >
                    <option value="Hindi">Hindi</option>
                    <option value="English">English</option>
                    <option value="French">French</option>
                    <option value="Marathi">Marathi</option>
                    <option value="Urdu">Urdu</option>
                    <option value="Punjabi">Punjabi</option>
                  </select>
                </div>
                <div>
                  <label className="fontW500_black">Email</label>
                  <InputField
                    text={"text"}
                    labelclass="fontW500_black"
                    classname="inputSize200"
                    value={employeeData.email}
                    onChange={handleInputChange ? handleInputChange("email") : undefined}
                    error={errors.email}
                  />
                </div>
              </div>
            </div>
            <h5 className="fontW500_blackbd">Contact Information</h5>
            <div className="d-flex flex-wrap p-2 gap-3">
              <div>
                <label className="fontW500_black">Address</label>
                <InputField
                  text={"text"}
                  labelclass="fontW500_black"
                  classname="inputSize200"
                  value={employeeData.address}
                  onChange={handleInputChange ? handleInputChange("address") : undefined}
                  error={errors.address}
                />
              </div>
              <div>
                <label className="fontW500_black">Phone</label>
                <InputField
                  text={"text"}
                  labelclass="fontW500_black"
                  classname="inputSize200"
                  value={employeeData.phone}
                  onChange={handleInputChange ? handleInputChange("phone") : undefined}
                  error={errors.phone}
                />
              </div>
            </div>
            <h5 className="fontW500_blackbd">Employee Information</h5>
            <div className="d-flex flex-wrap p-2 gap-3">
              <div>
                <label className="fontW500_black">Date Of Joining</label>
                <InputField
                  text={"date"}
                  labelclass="fontW500_black"
                  classname="inputSize200"
                  value={employeeData.doj}
                  onChange={handleInputChange ? handleInputChange("doj") : undefined}
                  error={errors.doj}
                />
              </div>
              <div className="d-flex flex-column w-25">
                <label className="fontW500_black" htmlFor="jobTitle">Job Title</label>
                <select
                  className="inputSize200 fontW500_black ps-2"
                  id="jobTitle"
                  name="jobtitle"
                  form="jobTitleForm"
                  value={employeeData.jobtitle}
                  onChange={handleInputChange ? handleInputChange("jobtitle") : undefined}
                >
                  <option value="UI/UX Designer">UI/UX Designer</option>
                  <option value="React Native Developer">React Native Developer</option>
                  <option value="Python">Python</option>
                  <option value="Java">Java</option>
                </select>
              </div>
              <div>
                <label className="fontW500_black">Department</label>
                <InputField
                  text={"text"}
                  labelclass="fontW500_black"
                  classname="inputSize200"
                  value={employeeData.department}
                  onChange={handleInputChange ? handleInputChange("department") : undefined}
                  error={errors.department}
                />
              </div>
            </div>
            <div className="d-flex py-3 gap-5">
              <div className="d-flex flex-row justify-content-center align-items-center">
                <input
                  type="file"
                  accept=".pdf, .docx, .doc"
                  onChange={handleResumeChange}
                  style={{ display: "none" }}
                  id="resumePickerInput"
                />
                <label
                  htmlFor="resumePickerInput"
                  className="btn btn-warning text-white"
                >
                  Upload Resume
                </label>
              </div>
            </div>
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-outline-warning"
              data-bs-dismiss="modal"
            >
              Close
            </button>
            <ButtonField
              type="button"
              classname="btn text-white btn-warning"
              label={`${heading === 'Add Employee' ? 'Save changes' : 'Update'}`}
              data_bs_dismiss="modal"
              onClick={handleSave}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddEmployeeForm;
