import React from 'react';

interface CardsProps {
  img?: string;
  heading?: string;
  paragraph?: string;
  label?: string;
}

const Cards = ({ img, heading, paragraph, label }: CardsProps) => {
  return (
    <div className="col-12 col-sm- col-md-6 col-lg-3 p-3 d-flex justify-content-center">
      <div className="card" style={{ width: '100%', maxWidth: '300px', maxHeight: '500px' }}>
        {img && (
          <img
            className="card-img-top"
            src={img}
            alt="Card image cap"
            style={{ width: '100%', height: 'auto', maxHeight: '152px', objectFit: 'cover' }}
          />
        )}
        <div className="card-body">
          <h5 className="card-title text-warning">{heading}</h5>
          <p className="card-text">{paragraph}</p>
          {label && (
            <a href="#" className="text-decoration-none text-warning d-flex justify-content-end">
              {label}
            </a>
          )}
        </div>
      </div>
    </div>
  );
};

export default Cards;
