import React, { useState } from "react";
import imagePath from "../constants/imagePath";
import InputField from "./InputField";
import "./Form.css";
import ButtonField from "./ButtonField";

interface FormProps {
  employeeData?: any;
  heading?: any;
  handleInputChange?: (field: any) => (event: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => void;
  handleProfileChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
  handleResumeChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
  onSave?: () => void;
}

const AddEmployeeForm: React.FC<FormProps> = ({
  heading,
  employeeData,
  handleProfileChange,
  handleResumeChange,
  handleInputChange,
  onSave
}) => {
  // State to hold error messages
  const [errors, setErrors] = useState({
    eid: '',
    firstname: '',
    lastname: '',
    dob: '',
    language: '',
    email: '',
    address: '',
    phone: '',
    doj: '',
    jobtitle: '',
    department: '',
  });

  // Validation function
  const validate = () => {
    const newErrors: any = {};
    if (!employeeData?.eid) newErrors.eid = "Employee Id is required.";
    if (!employeeData?.firstname) newErrors.firstname = "First Name is required.";
    if (!employeeData?.lastname) newErrors.lastname = "Last Name is required.";
    if (!employeeData?.dob) newErrors.dob = "Date of Birth is required.";
    if (!employeeData?.language) newErrors.language = "Language is required.";
    if (!employeeData?.email || !/^\S+@\S+\.\S+$/.test(employeeData.email)) newErrors.email = "Valid Email is required.";
    if (!employeeData?.address) newErrors.address = "Address is required.";
    if (!employeeData?.phone) newErrors.phone = "Phone number is required.";
    if (!employeeData?.doj) newErrors.doj = "Date of Joining is required.";
    if (!employeeData?.jobtitle) newErrors.jobtitle = "Job Title is required.";
    if (!employeeData?.department) newErrors.department = "Department is required.";

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validate()) {
      onSave?.();
    }
  };

  return (
    <div className="modal modal-xl" id="exampleModal" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">{heading}</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div className="modal-body d-flex flex-column">
            <div className="p-2">
