import React, { useEffect, useState } from "react";
import ButtonField from "../../components/ButtonField";
import Form from "../../components/Form";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";

const Newemployee = () => {
  const [hidden, setHidden] = useState<boolean>(true);
  const [allEmployees, setAllEmployees] = useState<any[]>([]);
  const [updateEmployeeData, setUpdateEmployeeData] = useState<any>(null);
  const [isEditMode, setIsEditMode] = useState<boolean>(false); // New state to track mode
  const [employeeData, setEmployeeData] = useState<any>({
    eid: "",
    firstname: "",
    lastname: "",
    language: "",
    email: "",
    address: "",
    phone: "",
    dob: "",
    doj: "",
    department: "",
    jobtitle: "",
    profile: "",
    resume: "",
  });

  useEffect(() => {
    fetchData();
  }, []);

  const handleProfileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEmployeeData({
      ...employeeData,
      profile: e.target.files && e.target.files[0],
    });
  };

  const handleResumeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEmployeeData({
      ...employeeData,
      resume: e.target.files && e.target.files[0],
    });
  };

  const handleInputChange =
    (field: keyof typeof employeeData) =>
    (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
      setEmployeeData({ ...employeeData, [field]: e.target.value });
    };

  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:8080/getAllEmployee");
      const result = await response.json();
      setAllEmployees(result);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  const handleSave = async () => {
    try {
      let formData = new FormData();
      formData.append("eid", employeeData.eid);
      formData.append("firstname", employeeData.firstname);
      formData.append("lastname", employeeData.lastname);
      formData.append("phone", employeeData.phone);
      formData.append("jobtitle", employeeData.jobtitle);
      formData.append("department", employeeData.department);
      formData.append("email", employeeData.email);
      formData.append("language", employeeData.language);
      formData.append("address", employeeData.address);
      formData.append("dob", employeeData.dob);
      formData.append("doj", employeeData.doj);
      formData.append("profile", employeeData.profile);
      formData.append("resume", employeeData.resume);

      const url = isEditMode
        ? `http://localhost:8080/updateEmployee/${employeeData.eid}` // Update URL
        : "http://localhost:8080/addEmployee"; // Add URL

      await fetch(url, {
        method: "POST",
        body: formData,
        redirect: "follow",
      })
        .then((response) => {
          response.json();
          fetchData();
          resetForm(); // Reset form after save
        })
        .catch((error) => console.error(error));
    } catch (error) {
      console.error("Error saving data: ", error);
    }
  };

  const resetForm = () => {
    setEmployeeData({
      eid: "",
      firstname: "",
      lastname: "",
      language: "",
      email: "",
      address: "",
      phone: "",
      dob: "",
      doj: "",
      department: "",
      jobtitle: "",
      profile: "",
      resume: "",
    });
    setIsEditMode(false);
    setUpdateEmployeeData(null);
  };

  const updateEmployee = (item: any) => {
    setIsEditMode(true); // Set edit mode
    setUpdateEmployeeData(item); // Populate form with existing data
    setEmployeeData(item); // Set form fields with employee data
  };

  const removeEmployee = async (eid: any) => {
    try {
      await axios.delete(`http://localhost:8080/removeEmployee/${eid}`);
      fetchData();
    } catch (error) {
      console.error("Error removing employee:", error);
    }
  };

  return (
    <>
      <ToastContainer />
      <div className="emp-main p-5">
        <ButtonField
          onClick={() => {
            setHidden(hidden ? false : true);
            resetForm(); // Reset form when opening for a new employee
          }}
          data_bs_target="#exampleModal"
          type="button"
          iconleftside={"+"}
          classname="btn btn-warning text-white"
          data_bs_toggle="modal"
          label="Add New"
        />

        <Form
          body={body[0]}
          heading={isEditMode ? "Update Employee" : "Add Employee"} // Dynamic heading
          printTitle={printTitle}
          updateData={updateEmployeeData}
          handleInputChange={handleInputChange}
          handleProfileChange={handleProfileChange}
          handleResumeChange={handleResumeChange}
          onSave={handleSave}
        />
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Name</th>
              <th scope="col">Eid</th>
              <th scope="col">Position</th>
              <th scope="col">Date Of Joining</th>
              <th scope="col">Salary</th>
              <th scope="col">Email Id</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {allEmployees.map((item: any, index: any) => (
              <tr key={index}>
                <th scope="row">
                  <img
                    src={item.profile}
                    style={{ width: "35px", borderRadius: "-50%" }}
                    alt="Profile"
                  />
                  {item.firstname}
                </th>
                <td>{item.eid}</td>
                <td>{item.jobtitle}</td>
                <td>{item.doj}</td>
                <td>{item.salary}</td>
                <td>{item.email}</td>
                <td className="align-content-center">
                  <div className="dropdown">
                    <button className="dropbtn btn btn-light">
                      <i className="fa fa-ellipsis-v"></i>
                    </button>
                    <div className="dropdown-content">
                      <a>
                        <button
                          className="btn-btn"
                          data-bs-target="#exampleModal"
                          data-bs-toggle="modal"
                          type="button"
                          style={{ border: "none" }}
                          onClick={() => {
                            setHidden(hidden ? false : true);
                            updateEmployee(item); // Edit employee
                          }}
                        >
                          <i className="fa fa-edit"></i>Edit
                        </button>
                      </a>
                      <a>
                        <button
                          className="btn-btn"
                          style={{ border: "none" }}
                          onClick={() => removeEmployee(item.eid)}
                        >
                          <i className="fa fa-trash-o"></i> Remove
                        </button>
                      </a>
                    </div>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Newemployee;
